import React, { useState } from "react";
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import CreateArea from "./CreateArea";
import CreateSection from "./CreateSection";
import Section from "./Section";

function App() {
  const [notes, setNotes] = useState([]);
  const [sections, setSections] = useState([]);

  function getFormattedDate(d){
    const day = d.getDate().toString();
      const numbMonth = (d.getMonth()+1);
      var month="";
      if(numbMonth>9)
        month = (numbMonth).toString();
      else
        month = "0"+(numbMonth).toString();
    const year = d.getFullYear().toString();
    return(year+"-"+month+"-"+day);
  }

  function addSection(newSection) {
    setSections(prevSections => {
      return [...prevSections, newSection];
    });
  }
  function deleteSection(id) {
    setSections(prevSections => {
      return prevSections.filter((sectionItem, index) => {
        return index !== id;
      });
    });
  }

  function addNote(newNote) {
    ///*
    if(newNote.date===""){
      newNote.date=getFormattedDate(new Date());
    }
    setNotes(prevNotes => {
      return [...prevNotes, newNote].sort((a, b) => 
      a.titlet - b.title
    )
  ;
    });
    //*/
    /*
    if(newNote.title!==""){
      if(newNote.date===""){
        newNote.date=getFormattedDate(new Date());
      }
      setNotes(prevNotes => {
        return [...prevNotes, newNote];
      });
    }else{
      alert("Please, set your new task.")
    }
    */
  }

  function deleteNote(id) {
    setNotes(prevNotes => {
      return prevNotes.filter((noteItem, index) => {
        return index !== id;
      });
    });
  }

  return (
    <div>
      <div style={{flexDirection:"row"}}>
        <Header />
        <CreateSection onAdd={addSection} />
      </div>
        <CreateArea onAdd={addNote} />

      {sections.map((SectioItem, index) => {
        return (
          <Section 
            key={index}
            id={index}  
            title={SectioItem.title}
            notes={notes.filter(notes => notes.section === SectioItem.title)}
            onDelete={deleteSection}
          />
        );
      })}

   {notes.map((noteItem, index) => {
        return (
          <Note
            key={index}
            id={index}
            date={noteItem.date}
            title={noteItem.title}
            content={noteItem.content}
            section={noteItem.section}
            onDelete={deleteNote}
          />
        );
      })}  

      <Footer />
    </div>
  );
}

export default App;
