import React, { useEffect, useState } from "react";
import DeleteIcon from "@material-ui/icons/Delete";
import Note from "./Note";

function Section(props) {

    const [originalNotes, setOriginalNotes] = useState(props.notes)

    const [notes, setNotes] = useState([]);

    const loadDataOnlyOnce = () => {
        setOriginalNotes(props.notes);
      }

    useEffect(() => { loadDataOnlyOnce(); }, [])

    useEffect(() => setOriginalNotes(props.notes), [props.notes]);

    function handleClick() {
      props.onDelete(props.id);
    }
    function deleteNote(id) {
        setOriginalNotes(prevNotes => {
            return prevNotes.filter((noteItem, index) => {
              return index !== id;
            });
        });
    }

    return (
      <div className="section"  
            style={{color: (props.title=="Done / Deleted" ? '#BDBFBD' : '#333'), }}
      >
        <button onClick={handleClick}>
          <DeleteIcon />
        </button> 
        <h1>{props.title}</h1>
        <div>
            {/* {(props.notes).sort((a, b) => a.title.localeCompare(b.title)).map((noteItem, index) => { */}
            {(originalNotes).sort((a, b) => new Date(...a.date.split('-').reverse()) - new Date(...b.date.split('-').reverse()))
                            .map((noteItem, index) => {
                return (
                    <Note
                        key={index}
                        id={index}
                        date={noteItem.date}
                        title={noteItem.title}
                        content={noteItem.content}
                        section={noteItem.section}
                        onDelete={deleteNote}
                    />
                );
            })}
        </div>

      </div>
    );
  }
  
  export default Section;
