import React, { useEffect, useState } from "react";
import DeleteIcon from "@material-ui/icons/Delete";
import Note from "./Note";

import Popup from 'reactjs-popup';
import 'reactjs-popup/dist/index.css';

function Section(props) {

    const [notes, setNotes] = useState([]);

    const loadDataOnlyOnce = () => {
        setNotes(props.notes);
      }

    useEffect(() => { loadDataOnlyOnce(); }, [])

    useEffect(() => setNotes(props.notes), [props.notes]);

    function handleClick() {
      props.onDelete(props.id);
    }

    function deleteNote(id) {
        setNotes(prevNotes => {
            return prevNotes.filter((noteItem, index) => {
              return (index !== id);
            });
        });
        props.onNotesDelete(id);
    }

    return (
      <div className="section"  
            style={{color: (props.title=="Done / Deleted" ? '#BDBFBD' : '#333'), }}
      >
        <Popup className="popup"  
        trigger=
                {<button >
                  <DeleteIcon />
                </button>}
                position="right center">
                <div>Delete this section?</div>
                <button>Cancel</button>
                <button onClick={handleClick}>Delete</button>
            </Popup>
        
        <h1>{props.title}</h1>
        
            {/* {(props.notes).sort((a, b) => a.title.localeCompare(b.title)).map((noteItem, index) => { */}
            {(notes).sort((a, b) => new Date(...a.date.split('-').reverse()) - new Date(...b.date.split('-').reverse()))
                            .map((noteItem, index) => {
                return (
                    <Note
                        key={index}
                        id={index}
                        date={noteItem.date}
                        title={noteItem.title}
                        content={noteItem.content}
                        section={noteItem.section}
                        /* onDelete={deleteNote(noteItem.title)}  */
                        onDelete={deleteNote}
                    />
                );
            })}
        

      </div>
    );
  }
  
  export default Section;
