import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from 'axios';
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import CreateArea from "./CreateArea";
import CreateSection from "./CreateSection";
import Section from "./Section";
import { wait } from "@testing-library/user-event/dist/utils";

function App() {
  const DONE = "Done / Deleted";
  const [notes, setNotes] = useState([]);
  const [sections, setSections] = useState([]);
  const [user,setUser] = useState();
  const [userLoggedIn,setUserLoggedIn] = useState(false);
  const [sectionLoaded,setSectionLoaded] = useState(false);

  function getFormattedDate(d){
    const day = d.getDate().toString();
      const numbMonth = (d.getMonth()+1);
      var month="";
      if(numbMonth>9)
        month = (numbMonth).toString();
      else
        month = "0"+(numbMonth).toString();
    const year = d.getFullYear().toString();
    return(year+"-"+month+"-"+day);
  }

  function addSection(newSection) {
    if(newSection.title!==""){
      setSections(prevSections => {
        return [...prevSections, newSection];
      });
    }else{
      alert("Please, give a name to this new section.")
    }
  }
  function deleteSection(id) {
    setSections(prevSections => {
      return prevSections.filter((sectionItem, index) => {
        return index !== id;
      });
    });
  }

  async function getUserFromApi(email, password){
      var data = JSON.stringify({"email":email,"password":password});
      var config = {
        method: 'get',
        url: 'http://localhost:8080/tkuser/find-by-email?email='+email+'&pssd='+password,
        headers: { 
          'Content-Type': 'application/json'
        },
        data : data
      };
      return axios(config)
      .then(function (response) {
        setUser(response.data);
        return{ userLoggedIn: ((user.email).includes('.com'))
        }
      })
      .catch(function (error) {
        console.log(error);
      });
  }

  async function getUser(email, password){
    //alert("email: "+email+" , pssd: :"+password)
    const {isLoggedIn} = await getUserFromApi(email,password);
    if (isLoggedIn === true) {
      setSections(user.sections);

      //navigation.navigate('Details');
    }
  }

  if(userLoggedIn){
    setSections(user.sections);
    setSectionLoaded(true);
  } 
  if(sectionLoaded){
    if((user.email).includes('.com')){
      axios
      .get('http://localhost:8080/tknote/find-by-user-id/'+user.id)
      .then((response) => {
         setNotes({
          title: response.data.title,
          content: response.data.content,
          section: response.data.section.title
        });
      })
      .catch((err) => {
         console.log(err);
      });
    } 
  }

  function addNote(newNote) {
    /*
    if(newNote.date===""){
      newNote.date=getFormattedDate(new Date());
    }
    setNotes(prevNotes => {
      return [...prevNotes, newNote];
    });
    */
    ///*
    if(newNote.title!==""){
      if(newNote.date===""){
        newNote.date=getFormattedDate(new Date());
      }
      setNotes(prevNotes => {
        return [...prevNotes, newNote];
      });
    }else{
      alert("Please, set your new task.")
    }
    //*/
  }

  function deleteNote(id) {
    setNotes(prevNotes => {
      return prevNotes.filter((noteItem, index) => {
        return index !== id;
      });
    });
  }

  return (
    <div>
      <div style={{flexDirection:"row"}}>
        <Header getUser={getUser}/>
        <CreateSection onAdd={addSection} />
      </div>
        <CreateArea onAdd={addNote} sections={sections}/>

        {/* <Section 
          key={DONE}
          id={DONE}  
          title={DONE}
          notes={notes.filter(notes => notes.section === DONE)}
        /> */}

      {sections.map((SectioItem, index) => {
        return (
          <Section 
            key={index}
            id={index}  
            title={SectioItem.title}
            notes={notes}//.filter(notes => (notes.section) === SectioItem.title)}
            onDelete={deleteSection}
            onNotesDelete={deleteNote}
          />
        );
      })}

      {notes.map((noteItem, index) => {
        return (
          <Note
            key={index}
            id={index}
            date={noteItem.date}
            title={noteItem.title}
            content={noteItem.content}
            section={noteItem.section}
            onDelete={deleteNote}
          />
        );
      })}

      <Footer />
    </div>
  );
}

export default (App);
